mapper.select:
SELECT c.challenge_id AS challengeId, c.user_id AS userId, c.title, c.description, c.starts_at AS startDate, c.ends_at AS endDate, c.status AS status,
/* 참여자 수 */ COALESCE(SUM( CASE WHEN c.status IN ('UPCOMING', 'ACTIVE') AND cp.status = 'JOINED' THEN 1
WHEN c.status = 'ENDED' AND cp.status IN ('COMPLETED', 'FAILED') THEN 1
ELSE 0 END ), 0) AS participantCount,
/* 현재 사용자 참여 여부 */ CASE WHEN EXISTS ( SELECT 1 FROM challenge_participants cp2 WHERE cp2.challenge_id = c.challenge_id AND cp2.user_id = #{userId} AND ( (c.status IN ('UPCOMING', 'ACTIVE') AND cp2.status = 'JOINED') OR (c.status = 'ENDED' AND cp2.status IN ('COMPLETED', 'FAILED')) ) ) THEN true ELSE false END AS joined
FROM challenges c LEFT JOIN challenge_participants cp ON cp.challenge_id = c.challenge_id
WHERE c.challenge_id = #{challengeId}
GROUP BY c.challenge_id

SELECT status
FROM challenges
WHERE challenge_id = #{challengeId}

SELECT starts_at
FROM challenges
WHERE challenge_id = #{challengeId}

SELECT user_id
FROM challenges
WHERE challenge_id = #{challengeId}
