mapper.select:
SELECT c.challenge_id AS challengeId, c.title, c.description, DATE_FORMAT(c.starts_at, '%Y-%m-%d') AS startDate, DATE_FORMAT(c.ends_at, '%Y-%m-%d') AS endDate,
cp.status AS status,
/* 진행률 계산 */ CASE WHEN c.status IN ('ENDED', 'CLOSED') THEN 1.0 WHEN c.status = 'ACTIVE' THEN ROUND( DATEDIFF(NOW(), c.starts_at) / GREATEST(DATEDIFF(c.ends_at, c.starts_at), 1), 2 ) ELSE 0.0 END AS progress
FROM challenge_participants cp JOIN challenges c ON cp.challenge_id = c.challenge_id
WHERE cp.user_id = #{userId}
ORDER BY c.starts_at DESC

SELECT COUNT(*)
FROM challenge_participants
WHERE challenge_id = #{challengeId}
  AND user_id = #{userId}

mapper.update:
UPDATE challenge_participants
SET status = #{status}
WHERE challenge_id = #{challengeId}

UPDATE challenge_participants
SET status = #{status}
WHERE challenge_id = #{challengeId} AND user_id = #{userId}
