mapper.insert:
INSERT INTO user_follows (
    follower_id,
    followee_id,
    status
)
VALUES (
    #{userId},
    #{targetUserId},
    #{status}
)

mapper.update:
UPDATE user_follows
SET status = #{status}
WHERE follow_id = #{requestId}
	AND followee_id = #{userId}
 	AND status = 'PENDING'

mapper.delete:
DELETE FROM user_follows
WHERE follow_id = #{requestId}
	AND follower_id = #{userId}
  	AND status = 'PENDING'

DELETE FROM user_follows
WHERE follower_id = #{userId}
  AND followee_id = #{targetUserId}
  AND status = 'ACCEPTED'

DELETE FROM user_follows
WHERE follower_id = #{followerId}
  AND followee_id = #{followeeId}
  AND status IN ('PENDING', 'ACCEPTED')

DELETE FROM user_follows
WHERE follower_id = #{userId}
  AND followee_id = #{targetUserId}
  AND status = 'BLOCKED'
